function Soldier(t,i){this.health=t,this.strength=i}function Viking(t,i,o){this.name=t,Soldier.call(this,i,o)}function Saxon(t,i){Soldier.call(this,t,i)}function War(){this.vikingArmy=[],this.saxonArmy=[]}Soldier.prototype.attack=function(){return this.strength},Soldier.prototype.receiveDamage=function(t){this.health-=t},Viking.prototype=Object.create(Soldier.prototype),Viking.prototype.constructor=Viking,Viking.prototype.receiveDamage=function(t){return this.health-=t,this.health>0?this.name+" has received "+t+" points of damage":this.name+" has died in act of combat"},Viking.prototype.battleCry=function(){return"Odin Owns You All!"},Saxon.prototype=Object.create(Soldier.prototype),Saxon.prototype.constructor=Saxon,Saxon.prototype.receiveDamage=function(t){return this.health-=t,this.health>0?"A Saxon has received "+t+" points of damage":"A Saxon has died in combat"},War.prototype.addViking=function(t){this.vikingArmy.push(t)},War.prototype.addSaxon=function(t){this.saxonArmy.push(t)},War.prototype.saxonAttack=function(){var t=Math.floor(Math.random()*this.saxonArmy.length),i=Math.floor(Math.random()*this.vikingArmy.length),o=this.vikingArmy[i].receiveDamage(this.saxonArmy[t].attack());return this.vikingArmy[i].health<=0&&this.vikingArmy.splice(i,1),o},War.prototype.showStatus=function(){return 0===this.saxonArmy.length?"Vikings have won the war of the century!":0===this.vikingArmy.length?"Saxons have fought for their lives and survive another day...":"Vikings and Saxons are still in the thick of battle."},War.prototype.vikingAttack=function(){var t=Math.floor(Math.random()*this.saxonArmy.length),i=Math.floor(Math.random()*this.vikingArmy.length),o=this.saxonArmy[t].receiveDamage(this.vikingArmy[i].attack());return this.saxonArmy[t].health<=0&&this.saxonArmy.splice(t,1),o};